import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  margin: 0;
  display: flex;
  flex-flow: column nowrap;
  position: relative;
`;

export const Item = styled.a`
  margin-bottom: 12px;
  box-shadow: none;
  height: 100%;
  border-radius: 10px;
  box-shadow: 0 2px 20px 0 rgb(0 0 0 / 10%);
  text-decoration: none;
  overflow: hidden;
  margin-bottom: 40px;
  transition: 0.3s;
  min-width: 260px;
`;

export const Background = styled.div`
  width: 100%;
  height: 140px;
  position: relative;
`;

export const Image = styled.img`
  width: 100%;
  border-radius: 10px;
  height: 140px;
`;

export const CardOverlay = styled.div`
  position: absolute;
  top: 0;
  z-index: 1;
  width: 100%;
  height: 100%;
  color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

export const Name = styled.h3`
    text-align: center;
    font-size: 28px;
    line-height: 1.2;
    font-weight: 400;
    color: #fff;
    margin: 0;
    padding: 0 8px;
    text-shadow: 0 1px 4px rgb(0 0 0 / 60%);

`;

export const CardContent = styled.div`
    padding: 8px 8px;
`;

export const About = styled.div`
    align-items: flex-start;
    display: flex;
    justify-content: space-between;
    font-size: 14px;
    line-height: 1.6;
    font-weight: 400;
    color: #000;
`;

export const AboutText = styled.div`
    display: flex;
    align-items: center;
`;

export const Text = styled.div`
    color: #000;
    font-size: 14px;
    color: #fff;
    font-weight: 500;
`;
