import styled from "styled-components";

export const Container = styled.div`
   display: flex;
    flex-flow: column nowrap;
    position: relative;

  h1 {
    font-size: 24px;
    padding: 24px 0 6px;
    margin: 0;
    font-weight: 600;
    font-size: 48px;
    color: #000;
  }

  h2 {
    font-size: 18px;
    padding: 0;
    margin: 0;
  }
  h3 {
    font-size: 16px;
    padding: 0 0 12px ;
    margin: 0;
  }

  p {
    font-size: 12px;
    padding: 0 ;
    margin: 0;
    width: 100%
  }
`;

export const Item = styled.div`
      display: flex;
    flex-direction: column;
    align-items: center;
    padding: 16px;

`;
