import styled from 'styled-components';

interface Button {
    width?: string;
}

export const ButtonStyled = styled.button`
        font-weight: 700;
    text-decoration: none;
    padding: 20px 60px;
    min-width: 120px;
    color: #fff;
    background-color: #14213D;
    border-radius: 100px;
    border: none;
    text-align: center;
    cursor: pointer;
    outline: none;
    max-width: 340px;
    box-sizing: border-box;
    padding: 16px 28px;
    width: 100%;
    justify-content: center;

    &:disabled {
        background-color: #e4e4e4;
    cursor: not-allowed;
    box-shadow: none;
    -webkit-backdrop-filter: none;
    backdrop-filter: none;
    }
    &[disabled] {
        background-color: #e4e4e4;
    cursor: not-allowed;
    box-shadow: none;
    -webkit-backdrop-filter: none;
    backdrop-filter: none;
    }
`;
