const getCurrentUser =() =>{
    return JSON.parse(localStorage.getItem("user")!);
};

const getToken = () => {
    return JSON.parse(localStorage.getItem('user')!);
}

const logout = () =>{
    localStorage.removeItem("user");
};

// eslint-disable-next-line import/no-anonymous-default-export
export default {
    getToken,
    getCurrentUser,
    logout
}
