import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

import PetDataService from "../../../../services/pet";
import AuthService from "../../services/auth"


 import { Container } from './styles';

const Details: React.FC = () => {



      interface Pet {
        name?: string;
        specie?: string;
        age?: string;
        description?: string;   
      };

      interface PetParams {
        id: string;
      }

      const params = useParams<PetParams>();
      const [currentPet, setCurrentPet] = useState<Pet>();
      const [message, setMessage] = useState("");




      useEffect(() => {
            PetDataService.get(params.id)
              .then(({data}) => {
                  setCurrentPet(data);
                  console.log(data);
          }).catch((e) => {
              console.log(e);
          });
        }, [params.id]);

      if (!currentPet) {
        return <p>Loading ...</p>;
      }


  return (
      <Container>
          <h1>
          {currentPet.name}
          </h1>
          <h2>
          {currentPet.specie}
          </h2>
          <h3>
          {currentPet.age}
          </h3>
          <p>
          {currentPet.description}
          </p>
      </Container>
  );
}

export default Details;
