import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import Button from '../../../../components/Button';

import Image from "../Image"
 import { Container, Info, Item, Title, Wrapper, ItemContact, NameContact, Loading } from './styles';

const Details: React.FC = () => {

      interface Pet {

        name?: string;
        specie?: string;
        age?: string;
        description?: string; 
        image: string;
        userId?: string;
      };

      interface User {

        firstName?: string;
        lastName?: string;
        phoneNumber?: string;
      };


      interface PetParams {
        id: string;
      }

      const params = useParams<PetParams>();
      const [pet, setPet] = useState<Pet>();
      const [user, setUser] = useState<User>();


      useEffect(() => {
        fetch("http://localhost:8080/api/pets/" + params.id)
      .then(response => response.json())
      .then(response => {
        setTimeout(() => {
          setPet(response)
        }, 2000)
      })
      .catch(e => console.log(e));


      }, [params.id]);

      const userToken = JSON.parse(localStorage.getItem("user")!);

      useEffect(() => {
        fetch("http://localhost:8080/api/users/" + pet?.userId,)
        .then((response) => response.json())
        .then((response) => {
         setUser(response);
        })
      }, [pet?.userId, userToken.token])


      if (!pet) {
        return <Loading  src="https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fwww.necoichi.com%2Ffiles%2Fuser%2FThemes%2FCatOneTheme%2FContent%2Fimages%2Floading.gif" alt="Cat Loading" /> ;
      }


  return (
    <div>
    <Image image={pet.image}></Image>
    <Wrapper>
      <Container>
        <Item>
          <Title>
          <h3>
          {pet.name}
          </h3>
          </Title>
          <Info>
          <p>
          {pet.specie}
          </p>
          <p>
          {pet.age}
          </p>
          <p>
          {pet.description}
          </p>
          </Info>
          </Item>
          <ItemContact>
            <NameContact>
              <p>{user?.firstName} {user?.lastName}</p>
            </NameContact>
            <Button width="100%">Call {user?.phoneNumber}</Button>
          </ItemContact>

      </Container>
      </Wrapper>
      </div>
  );
}

export default Details;
