import React from "react";
import Banner from "../../components/Banner";
import Header from "../../components/Header";
import { Form, Input } from "@rocketseat/unform";
import * as Yup from "yup";


const LoginSchema = Yup.object().shape({
    email: Yup.string()
      .email("Por favor, informe um e-mail válido")
      .required("O campo de e-mail é obrigatório"),

    password: Yup.string().required("O campo de senha é obrigatório")
  });

interface LoginPayload {
  email: string;
  password: string;
}

const Login = () => {

  const handleSubmit = (data: LoginPayload): void => {
    console.log(data);
  };

  return (
    <div>
      <Header></Header>
      <Banner></Banner>
      <Form schema={LoginSchema} onSubmit={handleSubmit}>
        <div>
          <Input name="email" placeholder="Seu e-mail" />
        </div>
        <div>
          <Input name="password" type="password" placeholder="Senha secreta" />
        </div>
        <button type="submit">Submit</button>
      </Form>
    </div>
  );
};

export default Login;
