import React, {useEffect, useState} from "react";
import { Container, TextLogout } from './styles';
import logo from '../../images/logo.svg';
import Button from '../Button';
import {getCurrentUser, getProfile, logout} from "../../services/auth";
import {Link} from "react-router-dom";


interface HeaderProps {
  background?: string;
}

function Header(props: HeaderProps) {



  interface Profile {
    firstName: String;
  }


  const [currentUser, setCurrentUser] = useState<String|undefined>(undefined); // mudei aqui a tipagem, tenta evitar any :
  const user = getCurrentUser();
  const [profile, setProfile] = useState<Profile>();


 if (!currentUser  && user) {
  setCurrentUser(user);
}

const handleLogout = () => {
  setCurrentUser(undefined) // bastava mudares aqui o estado :) 
  logout();
}



const test = getProfile();

  useEffect(() => {

    if (currentUser) {
      console.log(JSON.stringify(getProfile()))
      setProfile(getProfile() as any)
      console.log(profile)
    }

  }, [])

  return (
    <Container>
      <Link to="/">
       <img src={logo} alt="Logo Happy Pet" />
       </ Link>
       {currentUser ? (
         <div>
         <TextLogout onClick={handleLogout}>{profile?.firstName}</TextLogout>
         <Link to="pets/create/">
         <Button width="128px">Create Pet</Button>
         </Link>
        </div>
       ) : (
        <Link to="/login"> 
        <Button width="100%">Login</Button>
        </Link>
       )}
    </Container>
  );
};

export default Header;
