import React, { useState, useRef } from "react";
import Banner from "../../components/Banner";
import Header from "../../components/Header";


import { Container, FormItem, Image, Input, Form, Button } from "./styles";





const Login: React.FC = () => {

    const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = () => {
    const BaseUrl = 'http://127.0.0.1:8000/api/token/'
    fetch(BaseUrl, {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            email: email,
            password: password
        })

    })
        .then(res => res.json())
        .then((res) => {
            console.log(" data of response", res.detail);
            localStorage.setItem('access_token', res.access);
            localStorage.setItem('refresh_token', res.refresh);

        })

  }
  return (
      <>
      <Header />
      <Banner />
      <Container>
        <Form onSubmit={handleSubmit}>
          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/letter.svg" />
            <Input
              placeholder="Email"
              type="email"
              name="email"
              value={email}
            />
          </FormItem>

          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/lock.svg" />
            <Input
              placeholder="Password"
              type="password"
              name="password"
              value={password}
            />
          </FormItem>

          <Button
            type="submit"


          >
            Sign In
          </Button>
        </Form>
      </Container>

      </>
  );
}


export default Login;
