import React, { SyntheticEvent, useState } from 'react';
import Banner from "../../components/Banner";
import Header from "../../components/Header";
import { Redirect } from 'react-router-dom';


import { Container, FormItem, Image, Input, Form, Button } from "./styles";



const Login = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [redirect, setRedirect] = useState(false);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");

    const onSubmit = async (e: SyntheticEvent) => {
        e.preventDefault()

        setMessage('');
        setLoading(true);

        await fetch('http://localhost:8080/api/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        })
        .then((res) => {
          console.log(res);
          setRedirect(true);

        }).catch((error) =>{
            console.log(error);
        })


      }

      if (redirect) return <Redirect to='/' />

  return (
      <>
      <Header />
      <Banner />
      <Container>
        <Form onSubmit={onSubmit}>
          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/letter.svg" />
            <Input
              placeholder="Email"
              type="email"
              name="email"
              value={email}

              required
              onChange={(e) => setEmail(e.target.value)}
            />
          </FormItem>

          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/lock.svg" />
            <Input
              placeholder="Password"
              type="password"
              name="password"
              value={password}
              required
                onChange={(e) => setPassword(e.target.value)}
            />
          </FormItem>

          <Button
            type="submit"
            disabled={!email || !password}
             >
            Sign In
          </Button>
        </Form>
      </Container>

      </>
  );
}

export default Login;
