import { Container, Label, StyledSelect } from './styles'

interface Props {
    label?: string
    placeholder?: string
    noMargin?: boolean
    value: number
    min?: number
    options: readonly string[];
    onChange: (value: string) => void
}

export const Select: React.SFC<Props> = ({
    value,
    label,
    onChange,
    noMargin,
    options,
    ...selectProps
}) => (
    <Container noMargin={noMargin}>
        {label && <Label>{label}</Label>}

        <StyledSelect
            value={value}
            onChange={(e: any) => onChange(e.target.value)}
            {...selectProps}
        >
        {options.map((value) => (
        <option value={value} key={value}>
          {value}
        </option>
      ))}
      </StyledSelect>
    </Container>
)
