import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import { Item } from '../../../Home/components/ListingItem/styles';



 import { Container } from './styles';

const Details: React.FC = () => {

      interface Pet {
        name?: string;
        specie?: string;
        age?: string;
        description?: string;   
      };

      interface PetParams {
        id: string;
      }

      const params = useParams<PetParams>();
      const [pet, setPet] = useState<Pet>();


      useEffect(() => {
        fetch("http://localhost:8080/api/pets/" + params.id)
      .then(response => response.json())
      .then(response => {
        setPet(response)
      })
      .catch(e => console.log(e));

      }, [params.id]);

      if (!pet) {
        return <p>Loading ...</p>;
      }


  return (
      <Container>
        <Item>
          <h1>
          {pet.name}
          </h1>
          <h2>
          {pet.specie}
          </h2>
          <h3>
          {pet.age}
          </h3>
          <p>
          {pet.description}
          </p>
          </Item>
      </Container>
  );
}

export default Details;
