import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

import Image from "../Image"
 import { Container, Info, Item, Title, Wrapper } from './styles';

const Details: React.FC = () => {

      interface Pet {

        name?: string;
        specie?: string;
        age?: string;
        description?: string; 
        image: string;
        userId?: string;
      };

      interface User {

        firstName?: string;
        lastName?: string;
        phoneNumber?: string;
      };


      interface PetParams {
        id: string;
      }

      const params = useParams<PetParams>();
      const [pet, setPet] = useState<Pet>();
      const [user, setUser] = useState<User>();


      useEffect(() => {
        fetch("http://localhost:8080/api/pets/" + params.id)
      .then(response => response.json())
      .then(response => {
        setPet(response)
      })
      .catch(e => console.log(e));


      }, [params.id]);

      const userToken = JSON.parse(localStorage.getItem("user")!);


      fetch("http://localhost:8080/api/users/" + pet?.userId, {
        headers: {
          Authorization: `Bearer ${userToken.token}`,
        },
      })
      .then((response) => response.json())
      .then((response) => {
       console.log(response);
       setUser(response)
      })


      if (!pet) {
        return <p>Loading ...</p>;
      }


  return (
    <div>
    <Image image={pet.image}></Image>
    <Wrapper>
      <Container>
        <Item>
          <Title>
          <h3>
          {pet.name}
          </h3>
          </Title>
          <Info>
          <p>
          {pet.specie}
          </p>
          <p>
          {pet.age}
          </p>
          <p>
          {pet.description}
          </p>
          </Info>
          </Item>
          <div>
            <div>
              <p></p>
            </div>
          </div>

      </Container>
      </Wrapper>
      </div>
  );
}

export default Details;
