import React, {useState} from "react";
import { Container } from './styles';
import logo from '../../images/logo.svg';
import Button from '../Button';
import AuthorizationService from "../../services/auth";
import {Link} from "react-router-dom";


interface HeaderProps {
  background?: string;
}

function Header(props: HeaderProps) {

  const [currentUser, setCurrentUser] = useState<String|undefined>(undefined); // mudei aqui a tipagem, tenta evitar any :
  const user = AuthorizationService.getCurrentUser();


 if (!currentUser  && user) {
  setCurrentUser(user);
}

const handleLogout = () => {
  setCurrentUser(undefined) // bastava mudares aqui o estado :) 
  AuthorizationService.logout();
}

  return (
    <Container >
       <img src={logo} alt="Logo Happy Pet" />
       {currentUser ? (
        <Button width="128px" onClick={handleLogout}>Logout</Button>
       ) : (
        <Link to="/login"> 
        <Button width="100%">Login</Button>
        </Link>
       )}

    </Container>
  );
};

export default Header;
