import styled from 'styled-components';

interface Props{
  background?: string; 
}

export const Container = styled.div<Props>`
 display: flex;
  flex-direction: row;
  justify-content: space-between;
  padding: 10px 20px;
  width: 100%;
  min-height: 60px;
  margin: 0 auto;
  background-color: ${props => props.background || "#ffc244"};
  z-index: 19;
  box-sizing: border-box;
  align-items: flex-start;

  img {
    height: 52px;
    margin-top: 0;
  }
`;
