import React, { useState } from 'react';

 import { Container, Wrapper } from './styles';
 import {Input} from "../../components/Input"
import Header from '../../components/Header';
import Button from '../../components/Button';

const CreatePet: React.FC = () => {

  interface Pet {
    name: string;
    specie: string;
    age: string;
    gender:string;
    img: string;
    description:string;
  }
  const initialPetState = {
    name: "",
    specie: "",
    age: "",
    gender:"",
    img: "",
    description:""
  }
  const [pet, setPet] = useState<Pet>(initialPetState);

  const handleInputChange = (e: any) => {
    const { name, value } = e.target;
    setPet({ ...pet, [name]: value });
  };

  return (
    <>
    <Header></Header>
    <Wrapper> 
      <Container>
          <form>    
          <Input
                name="name"
                noMargin={false}
                onChange={handleInputChange}
                label="Name"
            />
             <Input
             name="specie"
                noMargin={false}
                onChange={handleInputChange}
                label="Specie"
                placeholder="( CAT | DOG | BIRD | FARM | HORSE | FISH )"
            />
             <Input
               name="name"
                noMargin={false}
                onChange={handleInputChange}
                label="Age"
                placeholder="( BABY | YOUNG | ADULT | SENIOR )"

            />
             <Input
             name="gender"
                noMargin={false}
                onChange={handleInputChange}
                label="Gender"
                placeholder="( MALE | FEMALE )"

            />
             <Input
             name="image"
                noMargin={false}
                onChange={handleInputChange}
                label="Link of Image"
            />
             <Input
             name="description"
                noMargin={false}
                onChange={handleInputChange}
                label="Description"
            />
            <Button 
            width="100%"
            type="submit"
             >
            Sign In
          </Button>
          </form>
          </Container>
      </Wrapper>
      </>

  );
}

export default CreatePet;
