import React, { SyntheticEvent, useState } from 'react';

 import { Container, ErrorMessage, Wrapper } from './styles';
 import {Input} from "../../components/Input"
import Header from '../../components/Header';
import Button from '../../components/Button';
import { Redirect } from 'react-router-dom';

const CreatePet: React.FC = () => {

  interface Pet {
    name: string;
    specie: string;
    age: string;
    gender:string;
    image: string;
    description:string;
  }
  const initialPetState = {
    name: "",
    specie: "",
    age: "",
    gender:"",
    image: "",
    description:""
  }
  const [pet, setPet] = useState<Pet>(initialPetState);
  const [redirect, setRedirect] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleInputChange = (e: any) => {
    const { name, value } = e.target;
    setPet({ ...pet, [name]: value });
  };

  const handleSubmit = async (e: SyntheticEvent) => {
    e.preventDefault();

    setMessage('');
    setLoading(true);

    const data  = {
      name: pet.name,
    specie: pet.specie,
    age: pet.age,
    gender: pet.gender,
    image: pet.image,
    description: pet.description
  };

  const user = JSON.parse(localStorage.getItem('user')!);

  fetch("http://localhost:8080/api/pets", {
    method: "POST",
    headers: { 
      "Content-Type": "application/json",
      'Authorization': `Bearer ${user.token}`
    },
    body: JSON.stringify(data)
  })
  .then(response => response.json())
  .then(response => {
    setRedirect(true);
  })
  .catch(e => {
    const errMessage = (e.response && e.response && e.response.message) || e.message || e.toString();
    setLoading(false);
      setMessage(errMessage);
    console.log(e);
  })
  }

  if (redirect) return <Redirect to='/' />


  return (
    <>
    <Header></Header>
    <Wrapper> 
      <Container>
          <form onSubmit={handleSubmit}>    
          <Input
                name="name"
                noMargin={false}
                onChange={handleInputChange}
                label="Name"
            />
             <Input
             name="specie"
                noMargin={false}
                onChange={handleInputChange}
                label="Specie"
                placeholder="( CAT | DOG | BIRD | FARM | HORSE | FISH )"
            />
             <Input
               name="name"
                noMargin={false}
                onChange={handleInputChange}
                label="Age"
                placeholder="( BABY | YOUNG | ADULT | SENIOR )"

            />
             <Input
             name="gender"
                noMargin={false}
                onChange={handleInputChange}
                label="Gender"
                placeholder="( MALE | FEMALE )"

            />
             <Input
             name="image"
                noMargin={false}
                onChange={handleInputChange}
                label="Link of Image"
            />
             <Input
             name="description"
                noMargin={false}
                onChange={handleInputChange}
                label="Description"
            />
             {message && (
          <ErrorMessage>{message}</ErrorMessage>
          )}
            <Button 
            width="100%"
            type="submit"
            disabled={ loading }
             >
            Sign In
          </Button>
          </form>
          </Container>
      </Wrapper>
      </>

  );
}

export default CreatePet;
