import React, { SyntheticEvent, useState } from 'react';
import Banner from "../../components/Banner";
import Header from "../../components/Header";
import { Redirect } from 'react-router-dom';
import AuthService from "../../services/auth"


import { Container, FormItem, Image, Input, Form, Button, ErrorMessage } from "./styles";



const Login = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [redirect, setRedirect] = useState(false);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");

    const onSubmit = async (e: SyntheticEvent) => {
        e.preventDefault()

        setMessage('');
        setLoading(true);

        AuthService.login(email, password).then((response) => {

          if (response.status >= 200 && response.status <= 299) {
            setRedirect(true);
          } else {
            const errMessage = response.message;
            setLoading(false);
            setMessage(errMessage);
          }




        });


      }

      if (redirect) return <Redirect to='/' />

  return (
      <>
      <Header />
      <Banner />
      <Container>
        <Form onSubmit={onSubmit}>
          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/letter.svg" />
            <Input
              placeholder="Email"
              type="email"
              name="email"
              value={email}
              required
              onChange={(e) => setEmail(e.target.value)}
            />
          </FormItem>

          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/lock.svg" />
            <Input
              placeholder="Password"
              type="password"
              name="password"
              value={password}
              required
                onChange={(e) => setPassword(e.target.value)}
            />
          </FormItem>
          {message && (
          <ErrorMessage>{message}</ErrorMessage>
          )}

          <Button
            type="submit"
            disabled={!email || !password  || loading }
             >
            Sign In
          </Button>
        </Form>
      </Container>

      </>
  );
}

export default Login;
