import styled from "styled-components";

export const Container = styled.div`
   display: flex;
    flex-flow: column nowrap;
    position: relative;





`;

export const Item = styled.div`
      display: flex;
    flex-direction: column;
    align-items: center;
    padding: 16px;
    border-radius: 10px;
    box-shadow: 0 2px 20px 0 rgb(0 0 0 / 10%);
    text-decoration: none;
    overflow: hidden;
    margin-bottom: 40px;
    transition: .3s;
    min-width: 260px;

`;

export const Title = styled.div`
   display: flex;
    justify-content: center;
    margin-bottom: 16px;

    h3 {
    font-size: 16px;
    padding: 0 0 12px ;
    margin: 0;
  }
`;

export const Info = styled.div`
   position: relative;
    display: flex;
    flex-direction: column;
    width: 95%;
    max-width: 300px;
    align-items: left;
    p {
    font-size: 12px;
    padding: 0 ;
    margin: 0;
    width: 100%
  }
`;
