import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import api from '../../../../services/api';


 import { Container } from './styles';

const Details: React.FC = () => {

    const initialPetState = {
        name: "",
        specie: "",
        age: "",
        description: ""
      };

      interface Pet {
        name?: string;
        specie?: string;
        age?: string;
        description?: string;   
      };

      interface PetParams {
        id: string;
      }

      const params = useParams<PetParams>();
      const [currentPet, setCurrentPet] = useState<Pet>(initialPetState);
      const [message, setMessage] = useState("");




      useEffect(() => {
        api.get(`/pets/${params.id}`).then(({ data }) => {
            setCurrentPet(data);
          });
        }, [params.id]);

      if (!currentPet) {
        return <p>Loading ...</p>;
      }


  return (
      <Container>
          <h1>
          {currentPet.name}
          </h1>
          <h2>
          {currentPet.specie}
          </h2>
          <h3>
          {currentPet.age}
          </h3>
          <p>
          {currentPet.description}
          </p>
      </Container>
  );
}

export default Details;
