import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  margin: 0;
  display: flex;
  flex-flow: column nowrap;
  position: relative;
`;

export const Item = styled.a`
  margin-bottom: 12px;
  box-shadow: none;
  height: 100%;
  border-radius: 10px;
  box-shadow: 0 2px 20px 0 rgb(0 0 0 / 10%);
  text-decoration: none;
  overflow: hidden;
  margin-bottom: 40px;
  transition: 0.3s;
  min-width: 260px;
`;

export const Background = styled.div`
  width: 100%;
  height: 140px;
  position: relative;
`;

export const Image = styled.img`
  width: 100%;
  border-radius: 10px;
  height: 140px;
`;

export const CardOverlay = styled.div`
  position: absolute;
  top: 0;
  z-index: 1;
  width: 100%;
  height: 100%;
  color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
