import React, { SyntheticEvent, useState } from 'react';
import Banner from "../../components/Banner";
import Header from "../../components/Header";
import { Redirect } from 'react-router-dom';


import { Container, FormItem, Image, Input, Form, Button, ErrorMessage } from "./styles";



const Login = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [redirect, setRedirect] = useState(false);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");

    const onSubmit = async (e: SyntheticEvent) => {
        e.preventDefault()

        setMessage('');
        setLoading(true);

        const data = {
          email: email,
          password: password
      }

      fetch("http://localhost:8080/api/auth/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      })
        .then(response => response.json())
        .then((response) => {
          console.log(response.status);
          if (response.status >= 200 && response.status <= 299) {
            if(response.token){ 
              localStorage.setItem("user", JSON.stringify(response));
              const user = JSON.parse(localStorage.getItem('user')!);
              console.log(user.token)
          }
          setRedirect(true);
          } else {
            const errMessage = response.message;
            setLoading(false);
            setMessage(errMessage);
            console.log(response)
          }



        });


      }

      if (redirect) return <Redirect to='/' />

  return (
      <>
      <Header />
      <Banner />
      <Container>
        <Form onSubmit={onSubmit}>
          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/letter.svg" />
            <Input
              placeholder="Email"
              type="email"
              name="email"
              value={email}
              required
              onChange={(e) => setEmail(e.target.value)}
            />
          </FormItem>

          <FormItem>
            <Image src="https://res.cloudinary.com/glovoapp/image/fetch///https://glovoapp.com/images/glyphs/lock.svg" />
            <Input
              placeholder="Password"
              type="password"
              name="password"
              value={password}
              required
                onChange={(e) => setPassword(e.target.value)}
            />
          </FormItem>
          {message && (
          <ErrorMessage>{message}</ErrorMessage>
          )}

          <Button
            type="submit"
            disabled={!email || !password  || loading }
             >
            Sign In
          </Button>
        </Form>
      </Container>

      </>
  );
}

export default Login;
