import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ListingItem from '../../components/ListingItem';

import { Container } from './styles';

const Pets: React.FC = () => {


    interface Pets {
        petId: string;
        name: string;
        age: string;
        specie: string;
        image: string;
      }


    interface PetParams {
        category: string;
      }

      const params = useParams<PetParams>();
      const [pets, setPets] = useState<Pets[]>([]);
      let category = params.category.toUpperCase();

      useEffect(() => {

        fetch("http://localhost:8080/api/pets/category?category=" + category)
        .then((response) => response.json())
        .then((response) => {
            setPets(response.results);
        })

      }, [params.category]) 

  return (
    <Container>
        {pets.map((pet) => {
              return (
              <div key={pet.petId}>
              <ListingItem image={pet.image} id={pet.petId} age={pet.age} specie={pet.specie} name={pet.name}></ListingItem>
              </div>
              )
            })}
    </Container>
  );
}

export default Pets;
